package com.panda.sport.merchant.common.enums;/** * @Author: Joken * @Description: 账户变更业务类型 * @Date: 2019/10/10 16:17 * @Param: * @Return: */public enum BusinessTypeEnum {    CHARGE(1, "充值"),    WITHDRAWAL(2, "提现"),    BET(3, "下注（减钱）"),    SETTLE(4, "结算（加钱）"),    REFUND(5, "退款"),    FREEZE(6, "冻结"),    GIFTS(7, "礼金"),    ;    private Integer code;    private String describe;    BusinessTypeEnum(Integer code, String describe) {        this.code = code;        this.describe = describe;    }    public Integer getCode() {        return code;    }    public String getDescribe() {        return describe;    }}