us, od.remark od_remark, od.cancel_type, od.settle_score, od.trade_type, od.risk_event, od.market_id, (CASE WHEN od.sport_id in (1, 2) and od.match_type = 2 THEN od.score_benchmark END) score_benchmark, (CASE WHEN o.manager_code not in (3,4) THEN (SELECT CONCAT(@a := ifNULL(t2.begin_time, 0), @c := ifNULL(t2.score, '0'), @f := ifNULL(sl1.${language}, '0'), @g := ifNULL(sl2.${language}, '0')) from s_match_info t2 left join s_language sl1 on sl1.name_code = t2.home_name_code left join s_language sl2 on sl2.name_code = t2.away_name_code where od.match_id = t2.id) WHEN o.manager_code = 3 THEN (select CONCAT(@a := ifNULL(t1.begin_time, 0), @c := ifNULL(t1.score_result, '0'), @f := ifNULL(sl1.zs, '0'), @g := ifNULL(sl2.zs, '0')) from s_virtual_match_info t1 left join s_language sl1 on sl1.name_code = t1.home_name_code left join s_language sl2 on sl2.name_code = t1.away_name_code where t1.id = od.match_id) WHEN o.manager_code = 4 THEN (select CONCAT(@a := ifNULL(t1.start_time, 0), @c := ifNULL(t1.score, '0'), @f := ifNULL(sl1.zs, '0'), @g := ifNULL(sl2.zs, '0')) from s_esport_matches t1 left join s_esport_language sl1 on sl1.name_code = t1.home_name_code left join s_esport_language sl2 on sl2.name_code = t1.away_name_code where t1.id = od.match_id) end) as temp, @a as begin_time, @c as score, @f as home_name, @g as away_name
,
