ORDER BY o1.id DESC LIMIT #{start},#{size} ) o ON o.order_no = od.order_no LEFT JOIN t_settle s ON s.order_no = o.order_no and s.last_settle = 1 LEFT JOIN t_user u ON u.uid = od.uid left join t_order_internationalize i on od.bet_no = i.bet_no and od.order_no = i.order_no LEFT JOIN t_merchant m ON u.merchant_code = m.merchant_code
LEFT JOIN ( SELECT ul.uid, ul.modify_time as vip_update_time FROM t_user_level_relation_history ul LEFT JOIN ( SELECT uid, max( id ) id FROM t_user_level_relation_history WHERE type = 3 GROUP BY uid ) a ON ul.id = a.id WHERE ul.type =3 and play_json = 'new=1' ) ul on ul.uid = u.uid
LEFT JOIN t_pre_settle t1 on t1.order_no = o.order_no LEFT JOIN t_pre_order t2 on t2.pre_order_no = t1.pre_order_no LEFT JOIN t_pre_order_detail t3 on t3.pre_order_no = t1.pre_order_no
ORDER BY ${sqlOrder} ${sortby} LIMIT #{start},#{size}

