mark od_remark, (CASE WHEN od.sport_id in (1, 2) and od.match_type = 2 THEN od.score_benchmark END) score_benchmark, od.cancel_type, od.settle_score, od.trade_type, od.risk_event, (CASE WHEN o.manager_code not in (3,4) THEN (SELECT CONCAT(@a := ifNULL(t2.begin_time, 0), @c := ifNULL(t2.score, '0'), @f := ifNULL(sl1.zs, '0'), @g := ifNULL(sl2.zs, '0')) from s_match_info t2 left join s_language sl1 on sl1.name_code = t2.home_name_code left join s_language sl2 on sl2.name_code = t2.away_name_code where od.match_id = t2.id) WHEN o.manager_code = 3 THEN (select CONCAT(@a := ifNULL(t1.begin_time, 0), @c := ifNULL(t1.score_result, '0'), @f := ifNULL(sl1.zs, '0'), @g := ifNULL(sl2.zs, '0')) from s_virtual_match_info t1 left join s_language sl1 on sl1.name_code = t1.home_name_code left join s_language sl2 on sl2.name_code = t1.away_name_code where t1.id = od.match_id) WHEN o.manager_code = 4 THEN (select CONCAT(@a := ifNULL(t1.start_time, 0), @c := ifNULL(t1.score, '0'), @f := ifNULL(sl1.zs, '0'), @g := ifNULL(sl2.zs, '0')) from s_esport_matches t1 left join s_esport_language sl1 on sl1.name_code = t1.home_name_code left join s_esport_language sl2 on sl2.name_code = t1.away_name_code where t1.id = od.match_id) end) as temp, @a as begin_time, @c as score, @f as home_name, @g as away_name
,
FROM t_order_detail od RIGHT JOIN (SELECT o1.id, o1.uid, o1.order_no, o1.create_time, o1.order_amount_total, o1.pre_bet_amount, o1.original_order_amount_total, o1.product_amount_total, o1.product_count, o1.series_type, o1.series_value, o1.ip, o1.ip_area, o1.device_type, o1.device_imei, o1.currency_code, o1.remark, o1.order_status, o1.ac_code, o1.manager_code, o1.pre_settle FROM t_order o1 where 1 = 1
and o1.pre_order = 1
and o1.create_time >= #{betStartTimeL}
and o1.pre_settle > 0
and o1.series_type = 1
AND o1.series_type <> 1
AND o1.vip_level = #{userVip}
AND o1.currency_code = #{currency}
AND o1.uid = #{userId}
AND o1.uid in
#{option}
AND o1.manager_code = 3
AND o1.manager_code != 3
AND o1.order_no = #{orderNo}
AND o1.order_no in
#{option}
AND o1.product_amount_total >= #{minBetAmount}
AND o1.product_amount_total <= #{maxBetAmount}
AND o1.order_status = #{orderStatus}
AND o1.order_status in
#{option}
and o1.order_no in (select distinct order_no from t_order_detail tod where 1 = 1
AND tod.match_id = #{matchId}
AND tod.sport_id = #{sportId}
AND tod.tournament_id = #{tournamentId}
AND tod.play_id = #{playId}
AND tod.play_id in
#{code}
AND tod.match_type = #{matchType}
AND tod.match_id in (select id from s_outright_match_info)
)
and o1.order_no in (select distinct order_no from t_settle s1 where s1.last_settle = 1
and s1.profit_amount >= #{minProfit}
AND s1.profit_amount <= #{maxProfit}
and s1.settle_type >2 and s1.settle_type not in(4,5)
and s1.out_come in
#{code}
)
and o1.merchant_id = #{merchantId}
and o1.merchant_id in
#{option}
and o1.uid in (select tu.uid from t_user tu
tu.username = #{userName}
tu.fake_name = #{fakeName}
)
and o1.order_no in (select distinct order_no from t_account_change_history tac where tac.create_time > #{betStartTimeL} and tac.after_transfer < 0 and tac.order_no is not null)
ORDER BY ${sqlOrder} ${sortby} LIMIT #{start},#{size} ) o ON o.order_no = od.order_no left join t_order_internationalize i on od.bet_no = i.bet_no and od.order_no = i.order_no LEFT JOIN t_settle s ON s.order_no = od.order_no and s.last_settle = 1 and s.bet_amount > 0 LEFT JOIN t_user u ON u.uid = od.uid LEFT JOIN t_merchant m ON u.merchant_code = m.merchant_code
LEFT JOIN (SELECT ul.uid, ul.modify_time as vip_update_time FROM t_user_level_relation_history ul LEFT JOIN ( SELECT uid, max( id ) id FROM t_user_level_relation_history WHERE type = 3 GROUP BY uid ) a ON ul.id = a.id WHERE ul.type =3 and play_json = 'new=1' ) ul on ul.uid = u.uid
LEFT JOIN t_pre_settle t1 on t1.order_no = o.order_no left join t_pre_order t2 on t1.pre_order_no = t2.pre_order_no LEFT JOIN t_pre_order_detail t3 on t3.pre_order_no = t1.pre_order_no
ORDER BY ${sqlOrder} ${sortby} LIMIT #{start},#{size}
